import React, { Component } from "react";
import { issue_book_list } from "../store/actions/bookActions";
import { getStudentByID } from "../store/actions/studentActions";
import { connect } from "react-redux";
import ReactTable from "react-table";
import "react-table/react-table.css";
import Axios from 'axios'

export class Issue_book_list extends Component {
  state = {
    get_data:{}
  };
  componentDidMount() {
    this.props.issue_book_list();
    
  }
  
  render() {
    const columns = [
   {
     Header: "SL",
     Cell: row => {
       return <div>{row.index + 1}</div>;
     },
     style: {
       textAlign: "center"
     }
   },
   {
     Header: "BOOK CODE",
     accessor: "i_book_id",
     filterable: true,
     style: {
       textAlign: "center"
     }
   },
   {
     Header: " STUDENT ID",
     accessor: (d)=>{
        Axios.get(`/student/${id}`)
          .then(response => {
            dispatch({
              type: Types.GET_STD_BY_ID,
              payload: { student: response.data }
            });
          })
          .catch(error => {
            console.log(error);
          });
     },
     style: {
       textAlign: "center"
     }
   },
   {
     Header: " ISSUE DATE",
     accessor: "i_date",
     style: {
       textAlign: "center"
     }
   },

   {
     Header: "RETURN DATE",
     accessor: "i_r_date",
     style: {
       textAlign: "center"
     }
   },

   {
     Header: " ACTIONS",
     style: {
       textAlign: "center"
     },

     Cell: props => {
       return this.props.getStudentByID(props.original.i_student_id);
     }
   }
 ];
   
    return (
      
      <div className="container">
        <div className="row">
          <div className="col-lg-12 offset-md-1">
            <div className="text-center">ISSUE BOOK LIST</div>
            <ReactTable
              columns={columns}
              data={this.props.book}
              defaultPageSize={5}
              noDataText={"No data found"}
            />
            
          </div>
        </div>
      </div>
    );
  }
}
const mapStateToProps = state => ({
  book: state.each_list
});

export default connect(
  mapStateToProps,
  { issue_book_list, getStudentByID }
)(Issue_book_list);
