import React, { Component } from 'react';

const initialState = {
  name: "",
  email: "",
  id: "",
  address: "",
  contact: "",
  isSubmitted: true,
  error: {},
  holaboi: ""
};

export class add_student extends Component {
         state = initialState;
         takeValueFromInput = e => {
           this.setState({
             [e.target.name]: e.target.value
           });
         };

         holaOnSubmit = e => {
           e.preventDefault();
           let { name, email, password, confirmPassword } = this.state;
           this.props.register(
             { name, email, password, confirmPassword },
             this.props.history
           );
         };
         render() {
           return (
             <div className="container">
               <div className="row">
                 <div className="col-md-8 offset-md-2">
                   <h1 className="text-center">User Register Form</h1>
                   {this.state.holaboi && (
                     <p className="text-success">{this.state.holaboi}</p>
                   )}
                   <form ref={this.myForm} onSubmit={this.holaOnSubmit}>
                     <div className="form-group">
                       <label htmlFor="name">Enter Your Name</label>
                       <input
                         type="text"
                         className={
                           error.name
                             ? "form-control is-invalid"
                             : "form-control"
                         }
                         name="name"
                         id="name"
                         value={name}
                         onChange={this.takeValueFromInput}
                       />
                       {error.name && (
                         <div className="invalid-feedback">{error.name}</div>
                       )}
                     </div>
                     <div className="form-group">
                       <label htmlFor="email">Enter Your Email</label>
                       <input
                         type="email"
                         className={
                           error.email
                             ? "form-control is-invalid"
                             : "form-control"
                         }
                         name="email"
                         id="email"
                         value={email}
                         onChange={this.takeValueFromInput}
                       />
                       {error.email && (
                         <div className="invalid-feedback">{error.email}</div>
                       )}
                     </div>
                     <div className="form-group">
                       <label htmlFor="password">Password</label>
                       <input
                         type="password"
                         className={
                           error.password
                             ? "form-control is-invalid"
                             : "form-control"
                         }
                         name="password"
                         id="password"
                         value={password}
                         onChange={this.takeValueFromInput}
                       />
                       {error.password && (
                         <div className="invalid-feedback">
                           {error.password}
                         </div>
                       )}
                     </div>

                     <div className="form-group">
                       <label htmlFor="cpass">Confirm Password</label>
                       <input
                         type="password"
                         className={
                           error.confirmPassword
                             ? "form-control is-invalid"
                             : "form-control"
                         }
                         name="confirmPassword"
                         id="cpass"
                         value={confirmPassword}
                         onChange={this.takeValueFromInput}
                       />
                       {error.confirmPassword && (
                         <div className="invalid-feedback">
                           {error.confirmPassword}
                         </div>
                       )}
                     </div>
                     <div className="form-group">
                       <button className="btn btn-sm btn-outline-danger">
                         Submit here
                       </button>
                     </div>
                   </form>
                 </div>
               </div>
             </div>
           );
         }
       }

export default add_student;
