import React, { Component } from 'react';
import {student_list} from '../store/actions/studentActions'
import {connect} from 'react-redux'
import ReactTable from "react-table";
import "react-table/react-table.css";
import axios from "axios";
export class Student_list extends Component {
      
         state = {
           posts: []
         };
         componentDidMount() {
           axios
             .get("https://jsonplaceholder.typicode.com/posts")
             .then(response => {
               this.setState({
                 posts: response.data
               });
             })
             .catch(error => console.log(error));
         }
         render() {
                    /*
           const columns = [
             {
               Header: "ID",
               accessor: "s_roll",
               filterable: true,
               style: {
                 textAlign: "center"
               }
             },
             {
               Header: " Name",
               accessor: "s_name",
               style: {
                 textAlign: "center"
               }
             },
             {
               Header: " Contact",
               accessor: "s_contact",
               style: {
                 textAlign: "center"
               }
             },
             {
               Header: "Email",
               accessor: "s_email",
               style: {
                 textAlign: "center"
               }
             },
             {
               Header: " Action",
               style: {
                 textAlign: "center"
               },
               Cell: props => {
                 return (
                   <button className="btn btn-xs btn-danger">Delete</button>
                 );
               }
             }
           ];
          */
          const columns = [
            {
              Header: "ID",
              accessor: "s_roll",
              filterable: true,
              style: {
                textAlign: "center"
              }
            },
            {
              Header: " Name",
              accessor: "s_name",
              style: {
                textAlign: "center"
              }
            },
            {
              Header: " Contact",
              accessor: "s_contact",
              style: {
                textAlign: "center"
              }
            },
            {
              Header: "Email",
              accessor: "s_email",
              style: {
                textAlign: "center"
              }
            },
            {
              Header: " Action",
              style: {
                textAlign: "center"
              },
              Cell: props => {
                return (
                  <button className="btn btn-xs btn-danger">Delete</button>
                );
              }
            }
          ];
          let { posts } = this.state;
                    return (
                      <div className="container">
                        <div className="row">
                          <div className="col-md-12">
                            <ReactTable
                              columns={columns}
                              data={this.state.posts}
                              defaultPageSize={5}
                              noDataText={"No data found"}
                            />
                          </div>
                        </div>
                      </div>
                    );
                  }
       }
const mapStateToProps = (state) =>(
    {
        std:state.std
    }
)

export default connect(mapStateToProps,{student_list})(Student_list);
