import React, { Component } from 'react';
import {student_list} from '../store/actions/studentActions'
import {connect} from 'react-redux'
import ReactTable from "react-table";
import "react-table/react-table.css";
export class Student_list extends Component {
         componentDidMount() {
           this.props.student_list();
         }
         render() {
             const columns = [
               {
                 Header: "ID",
                 accessor: "s_roll",
                 filterable: true,
                 style: {
                   textAlign: "center"
                 }
               },
               {
                 Header: " Name",
                 accessor: "s_name",
                 style: {
                   textAlign: "center"
                 }
               },
               {
                 Header: " Contact",
                 accessor: "s_contact",
                 style: {
                   textAlign: "center"
                 }
               },
               {
                   Header:"Email",
                   accessor:"s_email",
                   style:{
                       textAlign:"center"
                   }
               },
               {
                 Header: " Action",
                 style: {
                   textAlign: "center"
                 },
                 Cell: props => {
                   return (
                     <button className="btn btn-xs btn-danger"
                      
                     >
                       Delete
                     </button>
                   );
                 }
               }
             ];
             console.log(this.props.std.length);
           return (
             <div className="container">
               <div className="row">
                 <div className="col-md-12">
                   <ReactTable
                     columns={columns}
                     data={this.props.stds}
                     defaultPageSize={5}
                     noDataText={"Please wait"}
                   />

                   
                 </div>
               </div>
             </div>
           );
         }
       }
const mapStateToProps = (state) =>(
    {
        std:state.std
    }
)

export default connect(mapStateToProps,{student_list})(Student_list);
