import React, { Component } from "react";
import { issue_book_list } from "../store/actions/bookActions";
import { getStudentByID } from "../store/actions/studentActions";
import { connect } from "react-redux";
import ReactTable from "react-table";
import "react-table/react-table.css";
import axios from 'axios';

export class Issue_book_list extends Component {
         state = {
           std: [],
           s_student:[],
           posts:[]
         };
    
         componentDidMount() {
          this.get_element()
     
   
  
         }

get_element(){
  axios
    .get("/book/issue_book_list")
    .then(response => {
      let tasks = null;
      this.setState({
        std: response.data
      });
     let hola= response.data.map(x => {
        axios
        .get(`/student/${x.i_student_id}`)
         .then(res => {
        this.setState({
          posts: x
        });
        let div = document.createElement("li");
        //div.appendChild(body.s_roll);
        var text = `book: ${x.i_book_id},student: ${res.data.s_roll} `;
        let textNode = document.createTextNode(text);
        div.appendChild(textNode);
        return div
       
         })
        .catch(error => console.log(error));
      });
       hola.forEach(element => {
         document.getElementById("aaa").appendChild(element);
       });
    })
    .catch(error => console.log(error));
}


         render() {
           return (
             <div className="container">
               <div className="row mt-2">
                 <div className="col-md-12 offset-2">
                   <div id="aaa"></div>
                   <table className="table table-striped table-hover text-center">
                     <thead>
                       <tr className="btn-info">
                         <td>Student</td>
                         <td>Book</td>
                         <td>Issue Date</td>
                         <td>Return Date</td>
                       </tr>
                     </thead>

                     {this.state.std.map(data => (
                       
                       <tbody key={data.i_id}>
                         <tr>
                           <td>{data.i_student_id}</td>
                           <td>{data.i_book_id}</td>
                           <td>{data.i_date}</td>
                           <td>{data.i_r_date}</td>
                         </tr>
                       </tbody>
                     ))}

                     <tfoot>
                       <tr>
                         <td></td>
                         <td></td>
                         <td></td>
                       </tr>
                     </tfoot>
                   </table>
                 </div>
               </div>
               <div className="row mt-2">
                 <div className="col-md-12 offset-2">
                   <table className="table table-striped table-hover text-center">
                     <thead>
                       <tr className="btn-info">
                         <td>Student</td>
                         <td>Book</td>
                         <td>Issue Date</td>
                         <td>Return Date</td>
                       </tr>
                     </thead>

                     <tbody>
                       <tr>
                         <td>{this.state.posts.i_student_id}</td>
                         <td>{this.state.posts.i_book_id}</td>
                         <td>{this.state.posts.i_date}</td>
                         <td>{this.state.posts.i_r_date}</td>
                       </tr>
                     </tbody>

                     <tfoot>
                       <tr>
                         <td></td>
                         <td></td>
                         <td></td>
                       </tr>
                     </tfoot>
                   </table>
                 </div>
               </div>
             </div>
           );
         }
       }
const mapStateToProps = state => ({
  book: state.each_list,
  std: state.std.d
});

export default connect(
  mapStateToProps,
  { issue_book_list, getStudentByID }
)(Issue_book_list);
