import moment from "moment";
import React, { Component } from "react";
import { connect } from "react-redux";
import { r_book } from "../store/actions/bookActions";
const initialState = {

};

export class return_book extends Component {
         state = initialState;
         constructor() {
           super();

           this.state = {
             modalIsOpen: false
           };

           this.openModal = this.openModal.bind(this);
           this.afterOpenModal = this.afterOpenModal.bind(this);
           this.closeModal = this.closeModal.bind(this);
         }

         openModal() {
           this.setState({ modalIsOpen: true });
         }

         afterOpenModal() {
           // references are now sync'd and can be accessed.
           this.subtitle.style.color = "#f00";
         }

         closeModal() {
           this.setState({ modalIsOpen: false });
         }
         takeValueFromInput = e => {
           this.setState({
             [e.target.name]: e.target.value
           });
         };

         holaOnSubmit = e => {
           e.preventDefault();
           let { b_code, s_roll, u_i_r_date } = this.state;
           this.props.r_book(
             { b_code, s_roll, u_i_r_date },
             this.props.history
           );
         };
         componentDidUpdate() {
           setTimeout(
             () => this.setState({ success: this.props.book_success }),
             20
           );
           setTimeout(() => this.setState({ holaboi: this.props.book }), 20);
           setTimeout(() => this.setState({ error: this.props.book_er }), 20);
         }

         render() {
           let { b_code, s_roll, u_i_r_date, error } = this.state;
           return (
             <div className="container">
               <div className="row">
                 <div className="col-md-12 offset-md-1">
                   <h1 className="text-center">BOOK RETURN FORM</h1>

                   {this.state.holaboi && (
                     <p className="text-danger text-center">
                       {this.state.holaboi} <button>Pay</button>
                     </p>
                   )}
                   {this.state.success && (
                     <p className="text-success text-center" id="bro">
                       {this.state.success}
                     </p>
                   )}
                   <form onSubmit={this.holaOnSubmit}>
                     <input
                       type="hidden"
                       name="u_i_r_date"
                       value={u_i_r_date}
                       onChange={this.takeValueFromInput}
                     />
                     <table className="table ">
                       <tbody>
                         <tr>
                           <td>
                             <label
                               style={{ marginLeft: "50px" }}
                               htmlFor="code"
                             >
                               <b>BOOK CODE:</b>{" "}
                             </label>
                           </td>
                           <td>
                             <input
                               type="text"
                               className={
                                 error.code
                                   ? "form-control is-invalid"
                                   : "form-control"
                               }
                               name="b_code"
                               id="code"
                               value={b_code}
                               onChange={this.takeValueFromInput}
                             />
                             {error.code && (
                               <div className="invalid-feedback">
                                 {error.code}
                               </div>
                             )}
                           </td>
                           <td>
                             <label style={{ marginLeft: "50px" }} htmlFor="id">
                               <b>STUDENT ID:</b>{" "}
                             </label>
                           </td>
                           <td>
                             <input
                               type="text"
                               className={
                                 error.roll
                                   ? "form-control is-invalid"
                                   : "form-control"
                               }
                               name="s_roll"
                               id="id"
                               value={s_roll}
                               onChange={this.takeValueFromInput}
                             />{" "}
                             {error.roll && (
                               <div className="invalid-feedback">
                                 {error.roll}
                               </div>
                             )}
                           </td>
                         </tr>

                         <tr>
                           <td></td>
                           <td></td>
                           <td></td>
                           <td>
                             <button className="btn btn-sm btn-outline-info float-right">
                               RETURN BOOK
                             </button>
                           </td>
                         </tr>
                       </tbody>
                     </table>
                   </form>
                 </div>
               </div>
             </div>
           );
         }
       }

const mapStateToProps = state => ({
  book: state.bk.re_hola_success,
  book_er: state.bk.re_error,
  book_success: state.bk.re_success,
});

export default connect(
  mapStateToProps,
  { r_book }
)(return_book);
