import React, { Component } from 'react';
import { SearchStd } from "../store/actions/studentActions";
import { connect } from "react-redux";
const initialState = {
  search_input:"",
  error: {},
  holaboi: "",
  success: ""
};
export class home extends Component {
         state = initialState;
         takeValueFromInput = e => {
           this.setState({
             [e.target.name]: e.target.value
           });
         };

         holaOnSubmit = e => {
           e.preventDefault();
           let { search_input } = this.state;
           this.props.SearchStd({ search_input }, this.props.history);
         };
          componentDidUpdate() {
           setTimeout(
             () => this.setState({ error: this.props.std_error }),
             20
           );
          }
         render() {
           let { search_input, error } = this.state;
           return (
             <div className="container">
               <div className="row ml-4 mt-4 ">
                 <div className="col-md-12 offset-md-1">
                   <div
                     className="card"
                     style={{ backgroundColor: "rgba(50, 115, 200, 0.1)" }}
                   >
                     <div className="card-header text-center text-white bg-info">
                       <h2>SEARCH STUDENT</h2>
                     </div>
                     <div className="card-body">
                       <div className="row">
                         <div className="col-md-4 text-right mt-2">
                           <b>STUDENT ID / CONTACT NO </b>
                         </div>
                         <div className="col-md-8">
                           <form onSubmit={this.holaOnSubmit}>
                             <div className="input-group">
                               <div className="input-group-prepend">
                                 <span className="input-group-text" id="">
                                   <i className="fas fa-search"></i>
                                 </span>
                               </div>
                               <input
                                 type="text"
                                 className={
                                   error.search_student
                                     ? "form-control is-invalid"
                                     : "form-control"
                                 }
                                 name="search_input"
                                 value={search_input}
                                 onChange={this.takeValueFromInput}
                               />
                               {error.search_student && (
                                 <div className="invalid-feedback">
                                   {error.search_student}
                                 </div>
                               )}
                               <button className="btn btn-sm btn-outline-info float-right">
                                 Registration
                               </button>
                             </div>
                           </form>
                         </div>
                       </div>
                     </div>
                   </div>
                 </div>
               </div>
             </div>
           );
         }
       }
const mapStateToProps = state => ({
std_error: state.std.error
});

export default connect(null,{SearchStd})(home);
