import React, { Component } from 'react';
import {student_list} from '../store/actions/studentActions'
import {connect} from 'react-redux'
export class Student_list extends Component {
         componentDidMount() {
           this.props.loadTransactions();
         }
         render() {
           return (
             <div>
               {this.props.std.length > 0 ? (
                 <ul className="list-group">
                   {this.props.tran.map(transaction => (
                     <li key={transaction._id} className="list-group-item">
                       <p>Type: {transaction.type}</p>
                       <p>Amount: {transaction.amount}</p>
                       <button
                         onClick={() => {
                           this.props.removeTransaction(transaction._id);
                         }}
                       >
                         Delete
                       </button>
                     </li>
                   ))}
                 </ul>
               ) : (
                 <p>There is no transaction</p>
               )}
             </div>
           );
         }
       }
const mapStateToProps = (state) =>(
    {
        std:state.std
    }
)

export default connect(mapStateToProps,{student_list})(Student_list);
