import React, { Component } from 'react';

const initialState = {
  name: "",
  email: "",
  id: "",
  address: "",
  contact: "",
  isSubmitted: true,
  error: {},
  holaboi: ""
};

export class add_student extends Component {
         state = initialState;
         takeValueFromInput = e => {
           this.setState({
             [e.target.name]: e.target.value
           });
         };

         holaOnSubmit = e => {
           e.preventDefault();
           let { name, email, id, address,contact } = this.state;
           this.props.register(
             { name, email, id, address, contact },
             this.props.history
           );
         };
         render() {
               let { name, email, id, address, contact,error } = this.state;
           return (
             <div className="container">
               <div className="row">
                 <div className="col-md-8 offset-md-2">
                   <h1 className="text-center">Student Registration Form</h1>
                   {this.state.holaboi && (
                     <p className="text-success">{this.state.holaboi}</p>
                   )}
                   <form ref={this.myForm} onSubmit={this.holaOnSubmit}>
                     <div className="form-group">
                       <label htmlFor="id">Enter Your ID no</label>
                       <input
                         type="text"
                         className={
                           error.name
                             ? "form-control is-invalid"
                             : "form-control"
                         }
                         name="id"
                         id="id"
                         value={name}
                         onChange={this.takeValueFromInput}
                       />
                       {error.id && (
                         <div className="invalid-feedback">{error.id}</div>
                       )}
                     </div>
                     <div className="form-group">
                       <label htmlFor="name">Enter Your Name</label>
                       <input
                         type="text"
                         className={
                           error.name
                             ? "form-control is-invalid"
                             : "form-control"
                         }
                         name="name"
                         id="name"
                         value={name}
                         onChange={this.takeValueFromInput}
                       />
                       {error.name && (
                         <div className="invalid-feedback">{error.name}</div>
                       )}
                     </div>
                     <div className="form-group">
                       <label htmlFor="email">Enter Your Email</label>
                       <input
                         type="email"
                         className={
                           error.email
                             ? "form-control is-invalid"
                             : "form-control"
                         }
                         name="email"
                         id="email"
                         value={email}
                         onChange={this.takeValueFromInput}
                       />
                       {error.email && (
                         <div className="invalid-feedback">{error.email}</div>
                       )}
                     </div>
                     <div className="form-group">
                       <label htmlFor="contact">Enter Your Contact Number</label>
                       <input
                         type="text"
                         className={
                           error.contact
                             ? "form-control is-invalid"
                             : "form-control"
                         }
                         name="contact"
                         id="contact"
                         value={contact}
                         onChange={this.takeValueFromInput}
                       />
                       {error.contact && (
                         <div className="invalid-feedback">{error.contact}</div>
                       )}
                     </div>
                     

                     
                     <div className="form-group">
                       <button className="btn btn-sm btn-outline-danger">
                         Registration
                       </button>
                     </div>
                   </form>
                 </div>
               </div>
             </div>
           );
         }
       }

export default add_student;
