import React, { Component } from "react";
import { issue_book_list } from "../store/actions/bookActions";
import { getStudentByID } from "../store/actions/studentActions";
import { connect } from "react-redux";
import ReactTable from "react-table";
import "react-table/react-table.css";
import axios from 'axios';
import { NavLink } from "react-router-dom";
import Axios from "axios";

export class Issue_book_list extends Component {
         state = {
           std: [],
           s_student:[],
           posts:[]
         };
    
         componentDidMount() {
          this.get_element()
          this.props.issue_book_list()
          this.props.getStudentByID(1);
   
  
         }

get_element(){
  axios
    .get("/book/issue_book_list")
    .then(response => {
      let tasks = null;
      this.setState({
        std: response.data
      });
      response.data.map(x => {
        axios
        .get(`/student/${x.i_student_id}`)
         .then(res => {
        this.setState({
          posts: x
        });
        let tr = document.createElement("tr");
        //div.appendChild(body.s_roll);
          let td1 = document.createElement("td")
          let td2 = document.createElement("td")
          let td3 = document.createElement("td")
          let td4 = document.createElement("td")
          var student_a = document.createElement("A");
          student_a.innerHTML = "<strong>" + res.data.s_roll + "</strong>";
          student_a.setAttribute(
            "NavLink",
            `  to=/std/${res.data.s_id}  `
          );
          td1.innerHTML = x.i_book_id;
           td3.innerHTML = x.i_date;
           td4.innerHTML = x.i_r_date;
        
         
          
          //td1.appendChild(textNode1)
          td2.appendChild(student_a)
         // td3.appendChild(i_date)
          
          
          tr.append(td1,td2,td3,td4);
        //  console.log(tr)
          document.getElementById("aaa").appendChild(tr);
        
          })
          .catch(error => console.log(error));
      });
     
        
      
    })
    .catch(error => console.log(error));
}


         render() {
           return (
             <div className="container">
               <div className="row mt-2">
                 <div className="col-md-12">
                   <table className="table table-striped table-hover text-center">
                     <thead>
                       <tr className="btn-info">
                         <td>Book</td>
                         <td>Student</td>
                         <td>Issue Date</td>
                         <td>Return Date</td>
                       </tr>
                     </thead>

                     <tbody id="aaa"></tbody>

                     <tfoot>
                       <tr>
                         <td></td>
                         <td></td>
                         <td></td>
                       </tr>
                     </tfoot>
                   </table>
                   <ul className="list-group">
                     {this.props.book.map(
                       b => {
                          axios
                           .get(`/student/${b.i_student_id}`)
                           .then(res => (
                             console.log("fdfds"),
                             <li key={b.i_id} className="list-group-item">
                               <p>student: {b.i_student_id}</p>
                               <p>Book: {b.i_book_id}</p>
                             </li>
                       ))
                           }

                         
                           
                         
                       
                     )}
                   </ul>
                 </div>
               </div>
             </div>
           );
         }
       }
const mapStateToProps = state => ({
  book: state.each_list,
  std: state.std.d
});

export default connect(
  mapStateToProps,
  { issue_book_list, getStudentByID }
)(Issue_book_list);
