import React, { Component } from 'react';
import {student_list} from '../store/actions/studentActions'
import {connect} from 'react-redux'
import ReactTable from "react-table";
import "react-table/react-table.css";
export class Student_list extends Component {
         componentDidMount() {
           this.props.student_list();
         }
         render() {
             const columns = [
               {
                 Header: "ID",
                 accessor: "s_roll",
                 filterable: true,
                 style: {
                   textAlign: "center"
                 }
               },
               {
                 Header: " Name",
                 accessor: "s_name",
                 style: {
                   textAlign: "center"
                 }
               },
               {
                 Header: " Contact",
                 accessor: "s_contact",
                 style: {
                   textAlign: "center"
                 }
               },
               {
                   Header:"Email",
                   accessor:"s_email",
                   style:{
                       textAlign:"center"
                   }
               },
               {
                 Header: " Action",
                 style: {
                   textAlign: "center"
                 },
                 Cell: props => {
                   return (
                     <button
                       onClick={() => {
                         this.props.removeTransaction(props.original._id);
                       }}
                     >
                       Delete
                     </button>
                   );
                 }
               }
             ];
             console.log(this.props.std.length);
           return (
             <div>
               {this.props.std.length > 0 ? (
                 <ul className="list-group">
                   {this.props.std.map(transaction => (
                     <li  className="list-group-item">
                       <p>Type:</p>
                       <p>Amount: </p>
                       <button>
                         Delete
                       </button>
                     </li>
                   ))}
                 </ul>
               ) : (
                 <p>There is no transaction</p>
               )}
             </div>
           );
         }
       }
const mapStateToProps = (state) =>(
    {
        std:state.std
    }
)

export default connect(mapStateToProps,{student_list})(Student_list);
