import React, { Component } from "react";
import { issue_book_list } from "../store/actions/bookActions";
import { getStudentByID } from "../store/actions/studentActions";
import { connect } from "react-redux";
import ReactTable from "react-table";
import "react-table/react-table.css";
import axios from 'axios';
import { NavLink } from "react-router-dom";
import Axios from "axios";

export class Issue_book_list extends Component {
         state = {
           std: [],
           s_student:[],
           posts:[]
         };
    
         componentDidMount() {
        
          this.props.issue_book_list()
         
   
  
         }




         render() {
            const columns = [
              {
                Header: "SL",
                Cell: row => {
                  return <div>{row.index + 1}</div>;
                },
                style: {
                  textAlign: "center"
                }
              },
              {
                Header: "BOOK CODE",
                accessor: "i_book_id",
                filterable: true,
                style: {
                  textAlign: "center"
                }
              },

              {
                Header: " STUDENT ID",
                id: "i_student_id",
                accessor: d => {
                  // this.props.getStudentByID(d.i_student_id);
                  var hola = [];

                  Axios.get(`/student/${d.i_student_id}`).then(res => {
                    const std = res.data.s_roll;

                    // hola.push(res.data.s_roll);

                    //this.setState({ std });

                    // console.log(res.data.s_roll);
                    // console.log(hola);
                  });

                  console.log(this.state.std);
                  // const numbers = [1, 2, 3, 4, 5];
                  //return <div>sadfdfd</div>;
                  // console.dir(hola);
                  //  console.log(hola);
                  // console.log(numbers[1]);

                  return <div>{this.state.std}</div>;
                },
                style: {
                  textAlign: "center"
                }
              },
              {
                Header: " ISSUE DATE",
                accessor: "i_date",
                style: {
                  textAlign: "center"
                }
              },

              {
                Header: "RETURN DATE",
                accessor: "i_r_date",
                style: {
                  textAlign: "center"
                }
              },

              {
                Header: " ACTIONS",
                style: {
                  textAlign: "center"
                }
              }
            ];

           return (
             <div className="container">
               <div className="row mt-2">
                 <div className="col-md-12">
                   <table className="table table-striped table-hover text-center">
                     <thead>
                       <tr className="btn-info">
                         <td>Book</td>
                         <td>Student</td>
                         <td>Issue Date</td>
                         <td>Return Date</td>
                       </tr>
                     </thead>

                     <tbody id="aaa"></tbody>

                     <tfoot>
                       <tr>
                         <td></td>
                         <td></td>
                         <td></td>
                       </tr>
                     </tfoot>
                   </table>
                   <ul className="list-group">
                     {this.props.book.map(
                       b => {
                         return (
                           axios
                             .get(`/student/${b.i_student_id}`)
                             .then(res => <p>student: {res.data}</p>),
                           (
                             <li key={b.i_id} className="list-group-item">
                               <p>student: {b.s_roll}</p>
                               <p>Book: {b.i_book_id}</p>
                             </li>
                           )
                         );
                           }

                         
                           
                         
                       
                     )}
                   </ul>
                 </div>
               </div>
             </div>
           );
         }
       }
const mapStateToProps = state => ({
  book: state.each_list,
  std: state.std.d
});

export default connect(
  mapStateToProps,
  { issue_book_list, getStudentByID }
)(Issue_book_list);
