import moment from "moment";
import React, { Component } from "react";
import { connect } from "react-redux";
import { r_book } from "../store/actions/bookActions";
const initialState = {
  b_code: "",
  s_roll: "",
  u_i_r_date: moment().format("YYYY-MM-DD"),
  isSubmitted: true,
  isHola: true,
  error: {},
  holaboi: "ffff",
  success: ""
};

export class return_book extends Component {
         state = initialState;
         static getDerivedStateFromProps(nextProps, prevState) {
           if (
             JSON.stringify(nextProps.bk.re_error) !==
             JSON.stringify(prevState.re_error)
           ) {
             return {
               error: nextProps.bk.re_error
             };
           } else if (
             JSON.stringify(nextProps.book.re_hola_success) !==
             JSON.stringify(prevState.re_hola_success)
           ) {
             return {
               holaboi: nextProps.book.re_hola_success
             };
           } else if (
             JSON.stringify(nextProps.book.success) !==
             JSON.stringify(prevState.success)
           ) {
             return {
               success: nextProps.book.success
             };
           }

           return null;
         }
         takeValueFromInput = e => {
           this.setState({
             [e.target.name]: e.target.value
           });
         };

         holaOnSubmit = e => {
           e.preventDefault();
           let { b_code, s_roll, u_i_r_date } = this.state;
           this.props.r_book(
             { b_code, s_roll, u_i_r_date },
             this.props.history
           );
         };
         componentDidUpdate() {
           setTimeout(() => this.setState({ success: "" }), 20);
           setTimeout(() => this.setState({ holaboi: "" }), 20);
         }

         render() {
          
           let { b_code, s_roll, u_i_r_date, error, holaboi } = this.state;
           return (
             <div className="container">
               <div className="row">
                 <div className="col-md-12 offset-md-1">
                   <h1 className="text-center">BOOK RETURN FORM</h1>
                   {holaboi && (
                     <p className="text-danger text-center">{holaboi}</p>
                   )}
                   {this.state.success && (
                     <p className="text-success text-center" id="bro">
                       {this.state.success}
                     </p>
                   )}
                   <form onSubmit={this.holaOnSubmit}>
                     <input
                       type="hidden"
                       name="u_i_r_date"
                       value={u_i_r_date}
                       onChange={this.takeValueFromInput}
                     />
                     <table className="table ">
                       <tbody>
                         <tr>
                           <td>
                             <label
                               style={{ marginLeft: "50px" }}
                               htmlFor="code"
                             >
                               <b>BOOK CODE:</b>{" "}
                             </label>
                           </td>
                           <td>
                             <input
                               type="text"
                               className={
                                 error.code
                                   ? "form-control is-invalid"
                                   : "form-control"
                               }
                               name="b_code"
                               id="code"
                               value={b_code}
                               onChange={this.takeValueFromInput}
                             />
                             {error.code && (
                               <div className="invalid-feedback">
                                 {error.code}
                               </div>
                             )}
                           </td>
                           <td>
                             <label style={{ marginLeft: "50px" }} htmlFor="id">
                               <b>STUDENT ID:</b>{" "}
                             </label>
                           </td>
                           <td>
                             <input
                               type="text"
                               className={
                                 error.roll
                                   ? "form-control is-invalid"
                                   : "form-control"
                               }
                               name="s_roll"
                               id="id"
                               value={s_roll}
                               onChange={this.takeValueFromInput}
                             />{" "}
                             {error.roll && (
                               <div className="invalid-feedback">
                                 {error.roll}
                               </div>
                             )}
                           </td>
                         </tr>

                         <tr>
                           <td></td>
                           <td></td>
                           <td></td>
                           <td>
                             <button className="btn btn-sm btn-outline-info float-right">
                               RETURN BOOK
                             </button>
                           </td>
                         </tr>
                       </tbody>
                     </table>
                   </form>
                 </div>
               </div>
             </div>
           );
         }
       }

const mapStateToProps = state => ({
  book: state.bk
});

export default connect(
  mapStateToProps,
  { r_book }
)(return_book);
