import React, { Component } from "react";
import { issue_book_list } from "../store/actions/bookActions";
import { getStudentByID } from "../store/actions/studentActions";
import { connect } from "react-redux";
import ReactTable from "react-table";
import "react-table/react-table.css";
import Axios from 'axios';

export class Issue_book_list extends Component {
         state = {
           std: null
         };
         componentDidMount() {
           this.props.issue_book_list();
         }

         render() {
           const columns = [
             {
               Header: "SL",
               Cell: row => {
                 return <div>{row.index + 1}</div>;
               },
               style: {
                 textAlign: "center"
               }
             },
             {
               Header: "BOOK CODE",
               accessor: "i_book_id",
               filterable: true,
               style: {
                 textAlign: "center"
               }
             },

             {
               Header: " STUDENT ID",
               id: "i_student_id",
               accessor: d => {
                 const hola = [];
                 function put_data(x) {
                   console.log(x);
                 }
                 fetch(`/student/${d.i_student_id}`)
                   .then(response => response.json())
                   .then(body => {
                     //  hola .bind(body.s_roll);
                     //hola.bind();
                     //body.map(data=>console.log(data))
                     //  console.log(hola)

                    // let div = document.getElementById("get");
                     // div.appendChild(body.s_roll);
                     // var text = JSON.stringify(body.s_roll);
                     // let textNode = document.createTextNode(text);
                     //  div.appendChild(textNode);
                     //  div.innerHTML=body.s_roll

                     // document
                     //  .getElementById("bro")
                     // .innerHTML="maaal"

                     //put_data("fff")
                     // console.log(body.s_roll);
                    for(let i = 1;i<=1;i++){
                    if(i===1){
                      //this.setState({ std: body.s_roll });
                    }
                     console.log(body.s_roll)
                     
                    }
                    
                     // hola.push(body.s_roll);
                   // this.setState({})
                     // return div;
                     var text = "";

                     text += body.s_roll;
                     let textNode = document.createNodeIterator(text);
                     document.getElementById("bro").appendChild(textNode);
                    // console.log(text);
                   })

                   .catch(err => {});

                 // console.log(hola);

                 //  put_data("f");
                 return (
                   
                     <div id="bro">{}</div>
              
                 );
               },
               style: {
                 textAlign: "center"
               },
               width: 500
             },
             {
               Header: " ISSUE DATE",
               accessor: "i_date",
               style: {
                 textAlign: "center"
               }
             },

             {
               Header: "RETURN DATE",
               accessor: "i_r_date",
               style: {
                 textAlign: "center"
               }
             },

             {
               Header: " ACTIONS",
               style: {
                 textAlign: "center"
               }
             }
           ];

           return (
             <div className="container">
               <div className="row">
                 <div className="col-lg-12 offset-md-1">
                   <div className="text-center">ISSUE BOOK LIST</div>
                   <ReactTable
                     columns={columns}
                     data={this.props.book}
                     defaultPageSize={5}
                     noDataText={"No data found"}
                   />
                 </div>
               </div>
             </div>
           );
         }
       }
const mapStateToProps = state => ({
  book: state.each_list
});

export default connect(
  mapStateToProps,
  { issue_book_list, getStudentByID }
)(Issue_book_list);
