import React, { Component } from "react";
import { issue_book_list } from "../store/actions/bookActions";
import { getStudentByID } from "../store/actions/studentActions";
import { connect } from "react-redux";
import ReactTable from "react-table";
import "react-table/react-table.css";
import axios from 'axios';

export class Issue_book_list extends Component {
         state = {
           std: [],
           s_student:[],
           posts:[]
         };
    
         componentDidMount() {
          // this.props.issue_book_list();
          
    axios
      .get("/book/issue_book_list")
      .then(response => {
      console.log(response.data);
      let tasks= null
          const lis= response.data.map(x => {
          
            
             axios
               .get(`/student/${x.i_student_id}`)
               .then(res => {
                // console.log(res.data.s_roll);
                
                tasks = res.data.s_roll
               
               
               
              //  console.log(x);
              
              
                
                   let li = document.createElement("li");
                   //div.appendChild(body.s_roll);
                     var text = `book: ${x.i_book_id} ----student: ${res.data.s_roll} `;
                   let textNode = document.createTextNode(text);
                //   li.appendChild(textNode);
                  // div.innerHTML = body.s_roll;
               // console.log(li)
               
                   x.task = tasks;
                  
               })
              
               .catch(error => console.log(error));
                console.log(x);
                this.setState({
                  std: response.data,
                  posts: x
                });
            return x
               
          })
           //lis.forEach(hola => {
            // document.getElementById('ppp').appendChild(hola);
          // });
       
       
       
      })
      .catch(error => console.log(error));
     
   
  
         }




         render() {
           const columns = [
             {
               Header: "SL",
               Cell: row => {
                 return <div>{row.index + 1}</div>;
               },
               style: {
                 textAlign: "center"
               }
             },

             {
               Header: "BOOK CODE",
               accessor: "i_book_id",
               filterable: true,
               style: {
                 textAlign: "center"
               }
             },

             {
               Header: " STUDENT ID",
               id: "i_student_id",
               accessor: d => {
                 fetch(`/student/${d.i_student_id}`)
                   .then(response => response.json())
                   .then(body => {
                     //  hola .bind(body.s_roll);
                     //hola.bind();
                     //body.map(data=>console.log(data))
                     //  console.log(hola)

                     let div = document.getElementById();
                     // div.appendChild(body.s_roll);
                     var text = `ID no: ${body.s_roll} <br/>`;
                     let textNode = document.createTextNode(text);
                     div.appendChild(textNode);
                     //  div.innerHTML=body.s_roll
                     return div;
                     // document
                     //  .getElementById("bro")
                     // .innerHTML="hh"

                     //put_data("fff")
                     // console.log(body.s_roll);

                     //   this.setState({ std: body.s_roll });

                     // hola.push(body.s_roll);
                     // this.setState({})
                     // return div;
                     // var text = "";

                     // text += body.s_roll;
                     // let textNode = document.createTextNode(text);
                     //  document.getElementById("bro").appendChild(textNode);
                     // console.log(text);
                   })

                   .catch(err => {});
               },

               // console.log(hola);

               //  put_data("f");
               // d.i_student_id
               //  return <div>{this.props.std}</div>;

               style: {
                 textAlign: "center"
               },
               width: 500
             },
             {
               Header: " ISSUE DATE",
               accessor: "i_date",
               style: {
                 textAlign: "center"
               }
             },

             {
               Header: "RETURN DATE",
               accessor: "i_r_date",
               style: {
                 textAlign: "center"
               }
             },

             {
               Header: " ACTIONS",
               style: {
                 textAlign: "center"
               }
             },
             {
               Header: " Action",
               style: {
                 textAlign: "center"
               },
               Cell: props => {
                 return (
                   <button
                     onClick={() => {
                       this.props.removeTransaction(props.original._id);
                     }}
                   >
                     Delete
                   </button>
                 );
               }
             }
           ];

           return (
             <div className="container">
               <div className="row mt-2">
                 <div className="col-md-12 offset-2">
                   <table className="table table-striped table-hover text-center">
                     <thead>
                       <tr className="btn-info">
                         <td>Student</td>
                         <td>Book</td>
                         <td>Issue Date</td>
                         <td>Return Date</td>
                       </tr>
                     </thead>

                     {this.state.std.map(data => (
                       <tbody key={data.i_id}>
                         <tr>
                           <td>{data.i_student_id}</td>
                           <td>{data.i_book_id}</td>
                           <td>{data.i_date}</td>
                           <td>{data.i_r_date}</td>
                         </tr>
                       </tbody>
                     ))}

                     <tfoot>
                       <tr>
                         <td></td>
                         <td></td>
                         <td></td>
                       </tr>
                     </tfoot>
                   </table>
                 </div>
               </div>
               <div className="row mt-2">
                 <div className="col-md-12 offset-2">
                   <table className="table table-striped table-hover text-center">
                     <thead>
                       <tr className="btn-info">
                         <td>Student</td>
                         <td>Book</td>
                         <td>Issue Date</td>
                         <td>Return Date</td>
                       </tr>
                     </thead>

                     <tbody>
                       <tr>
                         <td>{this.state.posts.task}</td>
                         <td>{this.state.posts.i_book_id}</td>
                         <td>{this.state.posts.i_date}</td>
                         <td>{this.state.posts.i_r_date}</td>
                       </tr>
                     </tbody>

                     <tfoot>
                       <tr>
                         <td></td>
                         <td></td>
                         <td></td>
                       </tr>
                     </tfoot>
                   </table>
                 </div>
               </div>
             </div>
           );
         }
       }
const mapStateToProps = state => ({
  book: state.each_list,
  std: state.std.d
});

export default connect(
  mapStateToProps,
  { issue_book_list, getStudentByID }
)(Issue_book_list);
