import React, { Component } from "react";
import { issue_book_list } from "../store/actions/bookActions";
import { getStudentByID } from "../store/actions/studentActions";
import { connect } from "react-redux";
import ReactTable from "react-table";
import "react-table/react-table.css";
import axios from 'axios';

export class Issue_book_list extends Component {
         state = {
           std: [],
           s_student:[],
           posts:[]
         };
    
         componentDidMount() {
          this.get_element()
     
   
  
         }

get_element(){
  axios
    .get("/book/issue_book_list")
    .then(response => {
      let tasks = null;
      this.setState({
        std: response.data
      });
      response.data.map(x => {
        axios
        .get(`/student/${x.i_student_id}`)
         .then(res => {
        this.setState({
          posts: x
        });
        let tr = document.createElement("tr");
        //div.appendChild(body.s_roll);
          let td1 = document.createElement("td")
          let td2 = document.createElement("td")
        var text1 = `book: ${x.i_book_id}`;
        var text2 = `student: ${res.data.s_roll} `;
        let textNode1 = document.createTextNode(text1);
        let textNode2 = document.createTextNode(text2);
        let a1 = td1.appendChild(textNode1)
        let a2 = td2.appendChild(textNode2)
        tr.append(a1,a2);
        console.log(tr)
         document.getElementById("aaa").appendChild(tr);
       
         })
        .catch(error => console.log(error));
      });
     
        
      
    })
    .catch(error => console.log(error));
}


         render() {
           return (
             <div className="container">
               <div className="row mt-2">
                 <div className="col-md-12 offset-2">
                   
                   <table className="table table-striped table-hover text-center">
                     <thead>
                       <tr className="btn-info">
                         <td>Student</td>
                         <td>Book</td>
                         <td>Issue Date</td>
                         <td>Return Date</td>
                       </tr>
                     </thead>

                   
                       <tbody id="aaa">
                        
                       </tbody>
                   

                     <tfoot>
                       <tr>
                         <td></td>
                         <td></td>
                         <td></td>
                       </tr>
                     </tfoot>
                   </table>
                 </div>
               </div>
               <div className="row mt-2">
                 <div className="col-md-12 offset-2">
                   <table className="table table-striped table-hover text-center">
                     <thead>
                       <tr className="btn-info">
                         <td>Student</td>
                         <td>Book</td>
                         <td>Issue Date</td>
                         <td>Return Date</td>
                       </tr>
                     </thead>

                     <tbody>
                       <tr>
                         <td>{this.state.posts.i_student_id}</td>
                         <td>{this.state.posts.i_book_id}</td>
                         <td>{this.state.posts.i_date}</td>
                         <td>{this.state.posts.i_r_date}</td>
                       </tr>
                     </tbody>

                     <tfoot>
                       <tr>
                         <td></td>
                         <td></td>
                         <td></td>
                       </tr>
                     </tfoot>
                   </table>
                 </div>
               </div>
             </div>
           );
         }
       }
const mapStateToProps = state => ({
  book: state.each_list,
  std: state.std.d
});

export default connect(
  mapStateToProps,
  { issue_book_list, getStudentByID }
)(Issue_book_list);
