import React, { Component } from 'react';
import { connect } from "react-redux";
import { s_register } from "../store/actions/studentActions";
const initialState = {
  name: "",
  email: "",
  id: "",
  address: "",
  contact: "",
  isSubmitted: true,
  error: {},
  holaboi: ""
};

export class add_student extends Component {
         state = initialState;
         static getDerivedStateFromProps(nextProps, prevState) {
           if (
             JSON.stringify(nextProps.std.error) !==
             JSON.stringify(prevState.error)
           ) {
             return {
               error: nextProps.std.error
             };
           } else if (
             JSON.stringify(nextProps.std.hola_success) !==
             JSON.stringify(prevState.hola_success)
           ) {
             return {
               holaboi: nextProps.std.hola_success
             };
           }

           return null;
         }
         takeValueFromInput = e => {
           this.setState({
             [e.target.name]: e.target.value
           });
         };

         holaOnSubmit = e => {
           e.preventDefault();
           let { name, email, id, address, contact } = this.state;
           this.props.s_register(
             { name, email, id, address, contact },
             this.props.history
           );
         };
         render() {
           let { name, email, id, address, contact, error } = this.state;
           return (
             <div className="container">
               <div className="row">
                 <div className="col-md-12 offset-md-1">
                   <h1 className="text-center">Student Registration Form</h1>
                   {this.state.holaboi && (
                     <p className="text-success">{this.state.holaboi}</p>
                   )}
                   <form ref={this.myForm} onSubmit={this.holaOnSubmit}>
                     <table className="table ">
                       <tbody>
                         <tr>
                           <td>
                             <label style={{ marginLeft: "80px" }} htmlFor="id">
                               <b>ID no:</b>{" "}
                             </label>
                           </td>
                           <td>
                             <input
                               type="text"
                               className={
                                 error.id
                                   ? "form-control is-invalid"
                                   : "form-control"
                               }
                               name="id"
                               id="id"
                               value={id}
                               onChange={this.takeValueFromInput}
                             />
                             <br />
                             {error.id && (
                               <div className="invalid-feedback">
                                 {error.id}
                               </div>
                             )}
                           </td>
                           <td>
                             <label
                               style={{ marginLeft: "80px" }}
                               htmlFor="name"
                             >
                               <b>Name:</b>{" "}
                             </label>
                           </td>
                           <td>
                             <input
                               type="text"
                               className={
                                 error.name
                                   ? "form-control is-invalid"
                                   : "form-control"
                               }
                               name="name"
                               id="name"
                               value={name}
                               onChange={this.takeValueFromInput}
                             />{" "}
                             <br />
                             {error.name && (
                               <div className="invalid-feedback">
                                 {error.name}
                               </div>
                             )}
                           </td>
                         </tr>

                         <tr>
                           <td>
                             <label
                               style={{ marginLeft: "80px" }}
                               htmlFor="email"
                             >
                               <b>Email:</b>{" "}
                             </label>
                           </td>
                           <td>
                             <input
                               type="email"
                               className={
                                 error.email
                                   ? "form-control is-invalid"
                                   : "form-control"
                               }
                               name="email"
                               id="email"
                               value={email}
                               onChange={this.takeValueFromInput}
                             />{" "}
                             <br />
                             {error.email && (
                               <div className="invalid-feedback">
                                 {error.email}
                               </div>
                             )}
                           </td>
                           <td>
                             <label htmlFor="contact">
                               <b>Contact Number:</b>
                             </label>
                           </td>
                           <td>
                             <input
                               type="text"
                               className={
                                 error.contact
                                   ? "form-control is-invalid"
                                   : "form-control"
                               }
                               name="contact"
                               id="contact"
                               value={contact}
                               onChange={this.takeValueFromInput}
                             />{" "}
                             <br />
                             {error.contact && (
                               <div className="invalid-feedback">
                                 {error.contact}
                               </div>
                             )}
                           </td>
                         </tr>

                         <tr>
                           <td>
                             <label
                               style={{ marginLeft: "80px" }}
                               htmlFor="address"
                             >
                               <b>Address:</b>
                             </label>
                           </td>
                           <td>
                             <textarea
                               className={
                                 error.address
                                   ? "form-control is-invalid"
                                   : "form-control"
                               }
                               name="address"
                               id="address"
                               value={address}
                               onChange={this.takeValueFromInput}
                             ></textarea>
                             
                             {error.address && (
                               <div className="invalid-feedback">
                                 {error.address}
                               </div>
                             )}
                           </td>
                         </tr>
                         <tr>
                           <td></td>

                           <td>
                             <button className="btn btn-sm btn-outline-info float-right">
                               Registration
                             </button>
                           </td>
                         </tr>
                       </tbody>
                     </table>
                   </form>
                 </div>
               </div>
             </div>
           );
         }
       }

       const mapStateToProps = state => ({
  std: state.std
});

export default connect(mapStateToProps,{s_register})(add_student);
