import React, { Component } from "react";
import { issue_book_list } from "../store/actions/bookActions";
import { getStudentByID } from "../store/actions/studentActions";
import { connect } from "react-redux";
import ReactTable from "react-table";
import "react-table/react-table.css";
import axios from 'axios';
import { NavLink } from "react-router-dom";
import Axios from "axios";

export class Issue_book_list extends Component {
         state = {
           std: [],
           s_student:[],
           posts:[]
         };
    
         componentDidMount() {
        
          this.props.issue_book_list()
         
   
  
         }




         render() {
            const columns = [
              {
                Header: "SL",
                Cell: row => {
                  return <div>{row.index + 1}</div>;
                },
                style: {
                  textAlign: "center"
                }
              },
              {
                Header: "STUDENT ID",
                accessor: (d)=>{
                  return d.s_roll;
                },
                filterable: true,
                style: {
                  textAlign: "center"
                }
              },
              {
                Header: "BOOK CODE",
                accessor: "i_book_id",
                filterable: true,
                style: {
                  textAlign: "center"
                }
              },

              
              {
                Header: " ISSUE DATE",
                accessor: "i_date",
                style: {
                  textAlign: "center"
                }
              },

              {
                Header: "RETURN DATE",
                accessor: "i_r_date",
                style: {
                  textAlign: "center"
                }
              },

              {
                Header: " ACTIONS",
                style: {
                  textAlign: "center"
                }
              }
            ];

           return (
             <div className="container">
               <div className="row mt-2">
                 <div className="col-md-12">
                   <div className="text-center">ISSUE BOOK LIST</div>
                   <ReactTable
                     columns={columns}
                     data={this.props.book}
                     defaultPageSize={5}
                     noDataText={"No data found"}
                   />
                   <ul className="list-group">
                     {this.props.book.map(b => {
                       return (
                         axios
                           .get(`/student/${b.i_student_id}`)
                           .then(res => <p>student: {res.data}</p>),
                         (
                           <li key={b.i_id} className="list-group-item">
                             <p>student: {b.s_roll}</p>
                             <p>Book: {b.i_book_id}</p>
                           </li>
                         )
                       );
                     })}
                   </ul>
                 </div>
               </div>
             </div>
           );
         }
       }
const mapStateToProps = state => ({
  book: state.each_list,
  std: state.std.d
});

export default connect(
  mapStateToProps,
  { issue_book_list, getStudentByID }
)(Issue_book_list);
