import React, { Component } from 'react';
import { getStudentByID } from "../store/actions/studentActions";
import { connect } from "react-redux";
export class detail_student extends Component {
  state = {
    get_student : {}
  }
  componentDidMount(){
    this.props.getStudentByID(this.props.match.params.sid);
  }
  componentDidUpdate() {
  setTimeout(() => this.setState({ get_student: this.props.std }), 20);
  }
  render() {
    let {get_student} =this.state
    return (
      <div className="container">
        <div className="row ">
          <div className="col-md-12 text-center">
            STUDENT DETAILS
            </div>
            </div>
        <div className="row ">
          <div className="col-md-6">
              <table className="table table-striped table-hover table-condensed">
                <tbody>
                  <tr>
                    <td><b>Name:</b></td>
                    <td><b>{get_student.s_name}</b></td>
                  </tr>
                  <tr>
                    <td><b>ID no:</b></td>
                    <td><b>{get_student.s_roll}</b></td>
                  </tr>
                </tbody>
              </table>
          </div>
          <div className="col-md-6">
              <table className="table table-striped table-hover table-condensed">
                <tbody>
                  <tr>
                    <td><b>Name:</b></td>
                    <td><b>{get_student.s_name}</b></td>
                  </tr>
                  <tr>
                    <td><b>ID no:</b></td>
                    <td><b>{get_student.s_roll}</b></td>
                  </tr>
                </tbody>
              </table>
          </div>
        </div>
      </div>
    )

  }
}
const mapStateToProps = state => ({
  std: state.std.student
});
export default connect(mapStateToProps, {getStudentByID}) (detail_student);
