import React, { Component } from "react";
import { book_list } from "../store/actions/bookActions";
import { connect } from "react-redux";
import ReactTable from "react-table";
import "react-table/react-table.css";
export class Book_list extends Component {
  state = {};
  componentDidMount() {
    this.props.book_list();
  }
  render() {
    const columns = [
      {
        Header: "Book Code",
        accessor: "b_code",
        filterable: true,
        style: {
          textAlign: "center"
        }
      },
      {
        Header: " Name",
        accessor: "b_name",
        style: {
          textAlign: "center"
        }
      },
      {
        Header: " Publisher",
        accessor: "b_publisher",
        style: {
          textAlign: "center"
        }
      },
      {
        Header: "Quantity",
        accessor: "b_qty",
        style: {
          textAlign: "center"
        }
      },
      {
        Header: " Action",
        style: {
          textAlign: "center"
        },
        Cell: props => {
          return <button className="btn btn-xs btn-danger">Delete</button>;
        }
      }
    ];

    return (
      <div className="container">
        <div className="row">
          <div className="col-md-12">
            <ReactTable
              columns={columns}
              data={this.props.std}
              defaultPageSize={5}
              noDataText={"No data found"}
            />
            {this.props.std.length > 0 ? (
              <ul className="list-group">
                {this.props.std.map(transaction => (
                  <li key={transaction.s_id} className="list-group-item">
                    <p>Type: {transaction.s_name}</p>
                    <p>Amount: {transaction.s_email}</p>
                    <button>Delete</button>
                  </li>
                ))}
              </ul>
            ) : (
              <p>There is no transaction</p>
            )}
          </div>
        </div>
      </div>
    );
  }
}
const mapStateToProps = state => ({
  book: state.each_list
});

export default connect(
  mapStateToProps,
  { student_list }
)(Book_list);
