import React, { Component } from "react";
import { book_list } from "../store/actions/bookActions";
import { connect } from "react-redux";
import ReactTable from "react-table";
import "react-table/react-table.css";
export class Book_list extends Component {
  state = {};
  componentDidMount() {
    this.props.book_list();
  }
  render() {
    const columns = [
      {
        Header: "Book Code",
        accessor: "b_code",
        filterable: true,
        style: {
          textAlign: "center"
        }
      },
      {
        Header: " Name",
        accessor: "b_name",
        style: {
          textAlign: "center"
        }
      },
      {
        Header: " Publisher",
        accessor: "b_publishar",
        style: {
          textAlign: "center"
        }
      },
      {
        Header: "Quantity",
        accessor: "b_qty",
        style: {
          textAlign: "center"
        }
      },
      {
        Header: " Action",
        style: {
          textAlign: "center"
        },
        Cell: props => {
          return <button className="btn btn-sm btn-danger">Delete</button>;
        }
      }
    ];

    return (
      <div className="container">
        <div className="row">
          <div className="col-md-12">
            <ReactTable
              columns={columns}
              data={this.props.book}
              defaultPageSize={5}
              noDataText={"No data found"}
            />
          </div>
        </div>
      </div>
    );
  }
}
const mapStateToProps = state => ({
  book: state.each_list
});

export default connect(
  mapStateToProps,
  { book_list }
)(Book_list);
