import React, { Component } from "react";
import { issue_book_list } from "../store/actions/bookActions";
import { getStudentByID } from "../store/actions/studentActions";
import { connect } from "react-redux";
import ReactTable from "react-table";
import "react-table/react-table.css";
import Axios from 'axios';

export class Issue_book_list extends Component {
         state = {
           stds: [],
           s_student:{}
         };
         static getDerivedStateFromProps(nextProps, prevState) {
           if (
             JSON.stringify(nextProps.std.d) !==
             JSON.stringify(prevState.d)
           ) {
             return {
               s_student: nextProps.std.d
             };
           }
           

           return null;
         }
         componentDidMount() {
           this.props.issue_book_list();
          
    axios
      .get("http://localhost:4000/book/issue_book_list")
      .then(response => {
        this.setState({
          posts: response.data
        });
      })
      .catch(error => console.log(error));
  
         }

         render() {
           const columns = [
             {
               Header: "SL",
               Cell: row => {
                 return <div>{row.index + 1}</div>;
               },
               style: {
                 textAlign: "center"
               }
             },

             {
               Header: "BOOK CODE",
               accessor: "i_book_id",
               filterable: true,
               style: {
                 textAlign: "center"
               }
             },

             {
               Header: " STUDENT ID",
               id: "i_student_id",
               accessor: d => {
                 fetch(`/student/${d.i_student_id}`)
                   .then(response => response.json())
                   .then(body => {
                     //  hola .bind(body.s_roll);
                     //hola.bind();
                     //body.map(data=>console.log(data))
                     //  console.log(hola)

                     let div = document.getElementById("bro");
                     // div.appendChild(body.s_roll);
                     var text = `ID no: ${body.s_roll} <br/>`;
                     let textNode = document.createTextNode(text);
                     div.appendChild(textNode);
                     //  div.innerHTML=body.s_roll
                     return div;
                     // document
                     //  .getElementById("bro")
                     // .innerHTML="hh"

                     //put_data("fff")
                     // console.log(body.s_roll);

                     //   this.setState({ std: body.s_roll });

                     // hola.push(body.s_roll);
                     // this.setState({})
                     // return div;
                     // var text = "";

                     // text += body.s_roll;
                     // let textNode = document.createTextNode(text);
                     //  document.getElementById("bro").appendChild(textNode);
                     // console.log(text);
                   })

                   .catch(err => {});
               },

               // console.log(hola);

               //  put_data("f");
               // d.i_student_id
               //  return <div>{this.props.std}</div>;

               style: {
                 textAlign: "center"
               },
               width: 500
             },
             {
               Header: " ISSUE DATE",
               accessor: "i_date",
               style: {
                 textAlign: "center"
               }
             },

             {
               Header: "RETURN DATE",
               accessor: "i_r_date",
               style: {
                 textAlign: "center"
               }
             },

             {
               Header: " ACTIONS",
               style: {
                 textAlign: "center"
               }
             },
             {
               Header: " Action",
               style: {
                 textAlign: "center"
               },
               Cell: props => {
                 return (
                   <button
                     onClick={() => {
                       this.props.removeTransaction(props.original._id);
                     }}
                   >
                     Delete
                   </button>
                 );
               }
             }
           ];

           return (
             <div className="container">
               <div className="row">
                 <div className="col-lg-12 offset-md-1">
                   <div className="text-center">ISSUE BOOK LIST</div>
                   <ReactTable
                     columns={columns}
                     data={this.props.book}
                     defaultPageSize={5}
                     noDataText={"No data found"}
                   />
                   <div id = "bro"></div>
                   <ul className="list-group">
                     {this.props.book.map(b => (
                       <li key={b.i_id} className="list-group-item">
                         <p>std:<span id= "bro"></span></p>
                         <p>book: {b.i_book_id}</p>
                       </li>
                     ))}
                   </ul>
                 </div>
               </div>
             </div>
           );
         }
       }
const mapStateToProps = state => ({
  book: state.each_list,
  std: state.std.d
});

export default connect(
  mapStateToProps,
  { issue_book_list, getStudentByID }
)(Issue_book_list);
