import React, { Component } from "react";
import { issue_book_list } from "../store/actions/bookActions";
import { getStudentByID } from "../store/actions/studentActions";
import { connect } from "react-redux";
import ReactTable from "react-table";
import "react-table/react-table.css";
import Axios from 'axios';

export class Issue_book_list extends Component {
         state = {
           std: [],
           s_student:{}
         };
         static getDerivedStateFromProps(nextProps, prevState) {
           if (
             JSON.stringify(nextProps.std.d) !==
             JSON.stringify(prevState)
           ) {
             return {
               s_student: nextProps.std.d
             };
           }
           

           return null;
         }
         componentDidMount() {
           this.props.issue_book_list();
         }

         render() {
           const columns = [
             {
               Header: "SL",
               Cell: row => {
                 return <div>{row.index + 1}</div>;
               },
               style: {
                 textAlign: "center"
               }
             },
             {
               Header: "BOOK CODE",
               accessor: "i_book_id",
               filterable: true,
               style: {
                 textAlign: "center"
               }
             },

             {
               Header: " STUDENT ID",
               id: "i_student_id",
                  Cell: props => {
             return (
               <p
                 onClick={() => {
                   this.props.removeTransaction(props.original._id);
                 }}
               >
                 
               </p>
             );
           },
               accessor: d => {
                 const hola = [];

                 this.props.getStudentByID(d.i_student_id);

                 // console.log(hola);

                 //  put_data("f");

                 return this.props.std;
               },
               style: {
                 textAlign: "center"
               },
               width: 500
             },
             {
               Header: " ISSUE DATE",
               accessor: "i_date",
               style: {
                 textAlign: "center"
               }
           
             },

             {
               Header: "RETURN DATE",
               accessor: "i_r_date",
               style: {
                 textAlign: "center"
               }
             },

             {
               Header: " ACTIONS",
               style: {
                 textAlign: "center"
               }
             }
           ];

           return (
             <div className="container">
               <div className="row">
                 <div className="col-lg-12 offset-md-1">
                   <div className="text-center">ISSUE BOOK LIST</div>
                   <ReactTable
                     columns={columns}
                     data={this.props.book}
                     defaultPageSize={5}
                     noDataText={"No data found"}
                   />
                 </div>
               </div>
             </div>
           );
         }
       }
const mapStateToProps = state => ({
  book: state.each_list,
  std: state.std.d
});

export default connect(
  mapStateToProps,
  { issue_book_list, getStudentByID }
)(Issue_book_list);
